package com.yuesun.base.security.menu.entity;import java.util.ArrayList;import java.util.List;public class MenuVo {    private String id;    private String icon;    private String label;    private String path;    private List<MenuVo> subMenu = new ArrayList<>();    public String getPath() {        return path;    }    public void setPath(String path) {        this.path = path;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getIcon() {        return icon;    }    public void setIcon(String icon) {        this.icon = icon;    }    public String getLabel() {        return label;    }    public void setLabel(String label) {        this.label = label;    }    public List<MenuVo> getSubMenu() {        return subMenu;    }    public void setSubMenu(List<MenuVo> subMenu) {        this.subMenu = subMenu;    }}